# Displaying the highest-rated movies
# Sorting movies by rating
def top_rated_movies(df):
    return df[['original_title', 'vote_average']].sort_values(by='vote_average', ascending=False).head(10)

# Displaying the most popular movies
# Sorting movies by popularity
def top_popular_movies(df):
    return df[['original_title', 'popularity']].sort_values(by='popularity', ascending=False).head(10)
